mapper.select:
SELECT s.sharing_id, s.target_member_id, s.title, s.status, s.interest_num, s.created_at, s.review_flag, s.target_member_review_flag,
(SELECT COUNT(*) FROM comment c WHERE c.sharing_id = s.sharing_id AND c.del_flag IS NULL) AS commentCount,
(SELECT file_url FROM image i WHERE i.target_type = 'SHARING' AND i.target_id = s.sharing_id AND i.del_flag IS NULL ORDER BY i.image_id LIMIT 1) AS thumbnail,
COUNT(*) OVER() AS total_count FROM sharing s WHERE s.member_id = #{memberId}
AND s.title LIKE CONCAT('%', #{keyword}, '%')
AND s.status = #{status}
AND s.del_flag IS NULL ORDER BY s.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT s.sharing_id, s.target_member_id, s.title, s.status, s.interest_num, s.created_at, s.review_flag, s.target_member_review_flag,
(SELECT COUNT(*) FROM comment c WHERE c.sharing_id = s.sharing_id AND c.del_flag IS NULL) AS commentCount,
(SELECT file_url FROM image i WHERE i.target_type = 'SHARING' AND i.target_id = s.sharing_id AND i.del_flag IS NULL ORDER BY i.image_id LIMIT 1) AS thumbnail,
COUNT(*) OVER() AS total_count FROM sharing s WHERE s.target_member_id = #{memberId}
AND s.title LIKE CONCAT('%', #{keyword}, '%')
AND s.status = #{status}
AND s.del_flag IS NULL ORDER BY s.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(interest_id)
FROM interest
WHERE member_id = #{memberId}
